import React, { useState, useRef, useEffect } from 'react';
import cloudIcon from '../../assets/cloud.png';
import './fileupload.css';
import UploadedFile from '../UploadedFile/UploadedFile';

const FileUpload = ({ onChange, isEdit, modpack }) => {
    const [isDragging, setIsDragging] = useState(false);
    const [uploadedModpack, setUploadedModpack] = useState(null);
    const [uploadProgress, setUploadProgress] = useState(0);
    const fileInputRef = useRef(null);


    useEffect(() => {
      if (isEdit && modpack) {
        setUploadedModpack({
          name: modpack.modpack,
          size: modpack.size,
        })
      }
    })

    const handleDragEnter = () => {
        setIsDragging(true);
    };

    const handleDragLeave = () => {
        setIsDragging(false);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleModpack = (modpack) => {

      if (!modpack.name.toLowerCase().endsWith('.zip')) {
        console.error('Invalid file type. Please upload a ZIP file.');
        return;
      }
      setUploadedModpack(modpack);

      onChange('modpack', modpack);

      const reader = new FileReader();

      reader.onload = (e) => {
          console.log("Reading Started");
      };

      reader.onprogress = (event) => {
          if (event.lengthComputable) {
              const progress = (event.loaded / event.total) * 100;
              setUploadProgress(progress);
          }
      };

      reader.onloadend = () => {
          console.log('Reading finished');
        };
    
      reader.onload = (e) => {
      const dataUrl = e.target.result;
      };

      

      reader.readAsDataURL(modpack);
    };
    
    const handleDrop = (event) => {
      event.preventDefault();
      setIsDragging(false); 

      const files = event.dataTransfer.files;
      handleModpack(files[0]); 
    };

    const handleFileInputChange = (event) => {
        const files = event.target.files;
        handleModpack(files[0]); 
    };

    const handleClick = () => {
        fileInputRef.current.click();
    };

    const removeModpack = () => {
        setUploadProgress(0);
        setUploadedModpack(null);
        onChange('modpack', '');
        onChange('size', '');
    };

    return (
        <>
        {uploadProgress > 0 ? (
          <UploadedFile uploadedModpack={uploadedModpack} removeModpack={removeModpack} progress={uploadProgress} />
        ) : (
          <div
            className={`file-upload ${isDragging ? 'dragging' : ''}`}
            onDragEnter={handleDragEnter}
            onDragLeave={handleDragLeave}
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            onClick={handleClick}
          >
            <div className='file-upload-content'>
              <img src={cloudIcon} alt='Cloud upload icon' width={24} height={24} />
              <p className='file-upload-content-text'>
                Drop files here or click to upload
              </p>
            </div>
            <input
              type='file'
              accept='.zip'
              ref={fileInputRef}
              style={{ display: 'none' }}
              onChange={handleFileInputChange}
            />
          </div>
        )}
      </>
    );
};

export default FileUpload;
