import React, { useEffect, useRef, useState } from 'react';
import './dropdown.css';

const DropDown = ({ width = 'auto', height = 'auto', fontSize = 'inherit', options = [], value, onChange }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [isInputMode, setIsInputMode] = useState(false);
    const [inputValue, setInputValue] = useState('');
    const dropDownRef = useRef(null);
    const inputRef = useRef(null);

    const handleOptionsClick = (option) => {
        onChange(option);
        setIsOpen(false);
    };

    const handleClickOutside = (event) => {
        if (dropDownRef.current && !dropDownRef.current.contains(event.target)) {
            setIsOpen(false);
        }
    };

    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    const handleInputSubmit = () => {
        if (inputValue.trim() !== '') {
            onChange(inputValue.trim());
            setInputValue('');
            setIsInputMode(false);
        }
    };

    useEffect(() => {
        document.addEventListener('click', handleClickOutside);
        return () => {
            document.removeEventListener('click', handleClickOutside);
        };
    }, []);

    const toggleInputMode = () => {
        setIsInputMode(!isInputMode);
        setInputValue('');
    };

    return (
        <div className={`dropdown ${isOpen ? 'open' : ''}`} style={{ width }}>
            <div className='dropdown-selected-option' onClick={() => setIsOpen(!isOpen)}>
                {isInputMode ? (
                    <input
                        ref={inputRef}
                        type='text'
                        className='dropdown-input'
                        value={inputValue}
                        onChange={handleInputChange}
                        onBlur={handleInputSubmit}
                    />
                ) : (
                    <span>{value || 'Select a version'}</span>
                )}
                <span className={`dropdown-arrow ${isOpen ? 'open' : ''}`}> &#9660; </span>
                <button className='toggle-button' onClick={toggleInputMode}>
                    {isInputMode ? 'Done' : '+'}
                </button>
            </div>
            <div className={`dropdown-options ${isOpen ? 'open' : ''}`} ref={dropDownRef}>
                {options.map((option) => (
                    <div
                        key={option}
                        className={`dropdown-options-option ${option === value ? 'selected' : ''}`}
                        onClick={() => handleOptionsClick(option)}
                    >
                        {option}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default DropDown;
