import React from 'react';
import fileIcon from '../../assets/fileicon.png';
import deleteIcon from '../../assets/deleteicon.png';
import './uploadedfile.css';
const UploadedFile = ({ uploadedModpack, removeModpack, progress, noEdit }) => {

  const modpackSize = () => {
    let size = uploadedModpack.size;
    const suffixes = ['KB', 'MB', 'GB'];

    let i = -1;
    while (size >= 1000 && i < suffixes.length - 1) {
      size /= 1000;
      i++;
    }

    return `${size.toFixed(2)} ${suffixes[i]}`;
  };

  return (
    <>
    <div className='uploaded-file'>
      {noEdit ? (
       <div> </div> 
      ) : (
        <div> </div>
      )}
      <img src={fileIcon} alt='File Icon' width={26} height={33} />
      <div className='uploaded-file-content'>
        <div className='uploaded-file-content-info'>
          <p className='uploaded-file-content-info-text'>{uploadedModpack.name}  ({modpackSize()})</p>
          <img className='uploaded-file-content-info-delete' src={deleteIcon} alt='Delete Icon' width={16} height={17} onClick={removeModpack}/>
        </div>
        <progress value={progress} max='100'>
          {progress}%
        </progress>
      </div>
    </div>
    </>
  )
};

export default UploadedFile;
