import React, { useEffect, useState } from 'react';
import './createversion.css';
import { InputBox, ImageUpload, FileUpload, Button, DropDown} from '../exports';
import { uploadModpack, editModpack } from '../../util/api';
import { getVanillaVersions } from '../../util/vanillaApi';
import { addSmall, checkMark, fabricIcon, forgeIcon } from '../../assets/exports';

const CreateVersion = ({ close, modpack, isEdit, baseUrl, uploadsUrl }) => {

  const [newVersion, setNewVersion] = useState({
    name: '',
    size: '',
    id: Date.now(),
    zip: '',
    mcVersion: '',
    modLoader: '',
    modName: '',
  });
  

  const [vanillaVersions, setVanillaVersions] = useState([]);
  const [activeModName, setActiveModName] = useState('Fabric');
  const [versionInput, setVersionInput] = useState(true);
  const [selectedVersion, setSelectedVersion] = useState({});


  const handleInputChange = (fieldName, value) => {
    setModpackInfo((prevValues) => ({
      ...prevValues,
      [fieldName]: value,
    }));
  };

  const handleNewVersion = (fieldName, value) => {
    setNewVersion((prevValues) => ({
      ...prevValues,
      [fieldName]: value,
    }));
  };

  const handleCreate = async () => {
    try {
      let res;
      if (isEdit) {
        res = await editModpack(modpackInfo, baseUrl);
      } else {
        console.log(modpackInfo);
        res = await uploadModpack(modpackInfo, baseUrl);
      }
      console.log('Modpack uploaded successfully: ', res);
    } catch (error) {
      console.error('Error uploading modpack: ', error);
    } finally {
      close(null);
    }
  };

  const toggleModLoader = () => {
    const newLoaderName = activeModName === 'Fabric' ? 'Forge' : 'Fabric'
    handleInputChange('modName', newLoaderName);
    setActiveModName(newLoaderName);
  };

  const toggleInputMode = () => {
    if (!versionInput) {
      setModpackInfo(prevState => ({
        ...prevState,
        versions: [...prevState.versions, newVersion]
      }));
      setSelectedVersion(newVersion);
      setNewVersion({});
    }
  
    setVersionInput(!versionInput);
  }

  const fetchOptions = async () => {
    try {
      const vanVersions = await getVanillaVersions();

      setVanillaVersions(vanVersions);
    } catch (error) {
      console.error('Error fetching vanilla versions: ', error);
    }
  };

  useEffect(() => {
    fetchOptions();

    if (isEdit) {
      setModpackInfo(modpack);
      setActiveModName(modpack.modName);
    }
  }, []);
  return (
    <div className='create-version'>
            <div className='create-version-info-inputs-container'>
                <p className='create-version-info-inputs-headers'>Version</p>
                  <InputBox
                    width={200}
                    height={20}
                    fontSize='16px'
                    placeHolder='Enter Version'
                    value={newVersion.name}
                    onChange={(value) => handleNewVersion('name', value)}
                  />   
            </div>

            <div className='create-version-info-inputs-container'>
              <p className='create-version-info-inputs-headers'>Minecraft Version</p>
              <DropDown
                width={200}
                height={20}
                fontSize='16px'
                placeHolder='Enter Minecraft version'
                value={modpackInfo.mcVersion}
                onChange={(value) => handleNewVersion('mcVersion', value)}
                options={vanillaVersions}
              />
            </div>

            <div className='create-version-info-inputs-container'>
              <div className='create-version-info-inputs-headers-container'>
                <p className='create-version-info-inputs-headers'>{activeModName} Version</p>
                <img 
                  src={activeModName === 'Fabric' ? fabricIcon : forgeIcon}
                  alt={activeModName === 'Fabric' ? 'Fabric' : 'Forge'}
                  onClick={toggleModLoader}
                  width={24}
                  height={24}
                />
              </div>
              <InputBox
                width={200}
                height={20}
                fontSize='16px'
                placeHolder='Enter Mod Loader'
                value={modpackInfo.modLoader}
                onChange={(value) => handleNewVersion('modLoader', value)}
              />

          </div>
        <div className='create-version-file-upload'>
            <p className='create-version-file-upload-header'>Modpack Zip File</p>
            <FileUpload onChange={handleNewVersion} modpack={modpack} />
      </div>
    </div>
  )
};

export default CreateVersion;
