import React, { useState } from 'react';
import './button.css';

const Button = ({ onClick, type, width, height, text, fontSize }) => {
    const [isHovered, setIsHovered] = useState(false);

    const lightenColor = (color, percent) => {
        const num = parseInt(color.replace("#",""), 16);
        const amt = Math.round(2.55 * percent);
        const R = (num >> 16) + amt;
        const G = (num >> 8 & 0x00FF) + amt;
        const B = (num & 0x0000FF) + amt;
        return "#" + (0x1000000 + (R < 255 ? R < 1 ? 0 : R : 255) * 0x10000 + (G < 255 ? G < 1 ? 0 : G : 255) * 0x100 + (B < 255 ? B < 1 ? 0 : B : 255)).toString(16).slice(1);
    };

    const buttonStyle = {
        background: type === "confirm" ? (isHovered ? lightenColor('#0A8AE8', 20) : '#0A8AE8') : type === "delete" ? '#EB5757' : type === "cancel" ? 'transparent' : '#0A8AE8',
        width: width,
        height: height,
    };

    const textStyle = {
        fontSize: fontSize,
        color: type === "delete" ? '#000' : "#fff",
    };

    const handleMouseEnter = () => {
        setIsHovered(true); // Set hovered state to true on mouse enter
    };

    const handleMouseLeave = () => {
        setIsHovered(false); // Set hovered state to false on mouse leave
    };

    return (
        <div
            className='button'
            style={buttonStyle}
            onClick={onClick}
            onMouseEnter={handleMouseEnter} // Attach mouse enter event listener
            onMouseLeave={handleMouseLeave} // Attach mouse leave event listener
        >
            <p className='button-text' style={textStyle}> {text} </p>
        </div>
    )
};

export default Button;
