import React from 'react';
import './button.css';

const Button = ({ onClick, type, width, height, text, fontSize }) => {

    const buttonStyle = {
        background: type === "confirm" ? '#0A8AE8' : type === "delete" ? '#EB5757' : type === "cancel" ? '#0c0c0c' : '#0A8AE8',
        width: width,
        height: height,
    };

    const textStyle = {
      fontSize: fontSize,
      color: type === "delete" ? '#000' : "#fff",
    };

  return (
    <div className='button' style={buttonStyle} onClick={onClick}>
        <p className='button-text' style={textStyle}> {text} </p>
    </div>
  )
};

export default Button;
