import React, { useState } from 'react';
import cloudIcon from '../../assets/cloud.png';
import './fileupload.css';

const FileUpload = ({ onClick, onDrop }) => {
    const [isDragging, setIsDragging] = useState(false);

    const handleDragEnter = () => {
        setIsDragging(true);
    };

    const handleDragLeave = () => {
        setIsDragging(false);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setIsDragging(false);

        if (onDrop) {
            const files = e.dataTransfer.files;
            onDrop(files);
        }
    };

  return (
    <div className={`file-upload ${isDragging ? 'dragging' : ''}`}
         onDragEnter={handleDragEnter}
         onDragLeave={handleDragLeave}
         onDragOver={handleDragOver}
         onDrop={handleDrop}>
        <div className='file-upload-content'>
            <img src={cloudIcon} alt='Cloud upload icon' width={24} height={24} />
            <p className='file-upload-content-text'> Drop files here or click to upload</p> 
        </div>
    </div>
  )
};

export default FileUpload;
