import './App.css';
import CreateModpack from './components/CreateModpack/CreateModpack';
import { useEffect, useState } from 'react';
import Modpack from './components/Modpack/Modpack';
import { deleteModpack, getAllModpacks } from './util/api';
import NavBar from './components/NavBar/NavBar';
import ConfirmDelete from './components/ConfirmDelete/ConfirmDelete';

// fix button highlights
// add search bar 

function App() {

  const [showCreateModpack, setShowCreateModpack] = useState(false);
  const [showConfirmDelete, setShowConfirmDelete] = useState(false);
  const [allModpacks, setAllModpacks] = useState([null]);
  const [selectedModpack, setSelectedModpack] = useState(null);
  const [isEdit, setIsEdit] = useState(false);

  const toggleShowCreateModpack = (modpack) => {

    if (modpack.name !== null) {
      setSelectedModpack(modpack);
      setIsEdit(true);
    }
    else {
      setIsEdit(false);
    }

    setShowCreateModpack(!showCreateModpack);

    if (showCreateModpack) {
      fetchData();
    }
  };

  const toggleShowConfirmDelete = (modpack) => {
    setShowConfirmDelete(!showConfirmDelete);

    if (modpack) {
      setSelectedModpack(modpack);
    }
  };

  const fetchData = async () => {
    const modpacks = await getAllModpacks();

    setAllModpacks(modpacks);
  }


  const deletePack = async (modpack) => {
    const deletePack = await deleteModpack(modpack);

    if (deletePack)
    {
      console.log('Deleted modpack: ', modpack.id);
      setShowConfirmDelete(false);
      fetchData();
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="App">

      <NavBar toggleShowCreateModpack={toggleShowCreateModpack} />

    {showCreateModpack && (
       <div className='popup-overlay'>
        {isEdit ? (
          <CreateModpack close={toggleShowCreateModpack} modpack={selectedModpack} isEdit={isEdit} />
        ) : (
          <CreateModpack close={toggleShowCreateModpack} isEdit={isEdit} />
        )}
       </div>     
    )}

    {showConfirmDelete && (
      <div className='popup-overlay'>
        <ConfirmDelete toggleShowConfirmDelete={toggleShowConfirmDelete} deletePack={deletePack} selectedModpack={selectedModpack} />
      </div>
    )}

        {allModpacks[0] && (
          <div className='app-modpacks'>
            {allModpacks.map((modpack, index) => (
              index % 4 === 0 && (
                <div className="app-modpacks-row" key={`row-${index}`}>
                  {allModpacks.slice(index, index + 4).map((modpack, subIndex) => (
                      <Modpack modpack={modpack} deletePack={toggleShowConfirmDelete} toggleShowCreateModpack={toggleShowCreateModpack} />
                  ))}
                </div>
              )
            ))}
          </div>
        )}
    </div>
  );
}

export default App;
