import React from 'react';
import fileIcon from '../../assets/fileicon.png';
import deleteIcon from '../../assets/deleteicon.png';
import './uploadedfile.css';
const UploadedFile = ({ uploadedModpack, progress }) => {

  const modpackSize = () => {
    if (uploadedModpack.size > 1000) {
      const newSize = uploadedModpack.size / 1000;
      if (newSize > 1000) {
        return (newSize / 1000).toFixed(2) + ' MB';
      } else {
        return newSize.toFixed(2) + ' KB';
      }
    } else {
      return uploadedModpack.size + ' KB';
    }
  };

  return (
    <div className='uploaded-file'>
      <img src={fileIcon} alt='File Icon' width={26} height={33} />
      <div className='uploaded-file-content'>
        <div className='uploaded-file-content-info'>
          <p className='uploaded-file-content-info-text'>{uploadedModpack.name}  ({modpackSize()})</p>
          <img src={deleteIcon} alt='Delete Icon' width={16} height={17} />
        </div>
        <progress value={progress} max='100'>
          {progress}%
        </progress>
      </div>
    </div>
  )
};

export default UploadedFile;
