const baseUrl = 'http://127.0.0.1:3001/mmm/v1/';

export const uploadModpack = async (modpackInfo) => {
    try {
        const formData = new FormData();

        formData.append('name', modpackInfo.name);
        formData.append('version', modpackInfo.version);
        formData.append('description', modpackInfo.description);
        formData.append('mcVersion', modpackInfo.mcVersion);
        formData.append('fabricVersion', modpackInfo.fabricVersion);
        formData.append('modpack', modpackInfo.modpack);
        formData.append('thumbnail', modpackInfo.thumbnail);

        const res = await fetch(baseUrl, {
            method: 'POST',
            body: formData,
        });

        if (!res.ok) {
            throw new Error('Failed to upload file');
        }

        return res.json();
    } catch (error) {
        console.error('Error uploading file: ', error);
        throw error;
    }
};


export const editModpack = async (modpackInfo) => {
    try {
        const formData = new FormData();

        formData.append('name', modpackInfo.name);
        formData.append('version', modpackInfo.version);
        formData.append('description', modpackInfo.description);
        formData.append('mcVersion', modpackInfo.mcVersion);
        formData.append('fabricVersion', modpackInfo.fabricVersion);
        formData.append('modpack', modpackInfo.modpack);
        formData.append('thumbnail', modpackInfo.thumbnail);

        const res = await fetch(`${baseUrl}/${modpack.id}`, {
            method: 'PUT',
            body: formData,
        });

        if (!res.ok) {
            throw new Error('Failed updating file');
        }

        return res.json();
    } catch (error) {
        console.error('Error updating file: ', error);
        throw error;
    }
}


export const getAllModpacks = async () => {
    try {
        const res = await fetch(baseUrl, {
            method: 'GET',
        });

        if (!res.ok) {
            throw new Error('Failed to fetch modpacks');
        }

        return res.json();
    } catch (error) {
        console.error('Error fetching modpacks: ', error);
        throw error;
    }
}

export const deleteModpack = async (modpack) => {
    try {
        const res = await fetch(`${baseUrl}/${modpack.id}`, {
            method: 'DELETE',
        });

        if (!res.ok) {
            throw new Error('Failed to delete modpack: ', modpack.id);
        }

        return res.json();
    } catch (error) {
        console.error('Error deleting modpack: ', error);
        throw error;
    }
}
