import logo from './logo.svg';
import './App.css';
import CreateModpack from './components/CreateModpack/CreateModpack';
import { useState } from 'react';
import ToolBarButton from './components/ToolBarButton/ToolBarButton';
import add from './assets/add.png';
import Modpack from './components/Modpack/Modpack';

// fix button highlights

function App() {

  const [showCreateModpack, setShowCreateModpack] = useState(false);

  const toggleShowCreateModpack = () => {
    setShowCreateModpack(!showCreateModpack);
  };


  const testModpack = {
    name: "The MMM 3",
    version: "v.1.0.0.0",
    description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.Lorem ipsum dolor sit amet, consectetur adipiscing elit.Lorem ipsum dolor sit amet, consectetur adipiscing elit.Lorem ipsum dolor sit amet, consectetur adipiscing elit.Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
    modpack: {
      name: "test modpack name",
      size: 100210232,
    }
  };

  return (
    <div className="App">
      <ToolBarButton text={"Create New Modpack"} icon={add} onClick={toggleShowCreateModpack} />

    {showCreateModpack && (
       <div className='app-create-modpack-container'>
       <CreateModpack close={toggleShowCreateModpack} />
     </div>     
    )}

    < Modpack modpack={testModpack}/>
    </div>
  );
}

export default App;
