import React, { useState } from 'react';
import './dropdown.css';

const DropDown = ({ width, height, fontSize, options, value, onChange}) => {
    const [isOpen, setIsOpen] = useState(false);

    const handleOptionsClick = (option) => {
        onChange(option);
        setIsOpen(false);
    };

    const dropDownBoxStyle = {
        width: width,
        height: height,
        fontSize: fontSize,
    };

  return (
    <div className='dropdown' style={dropDownBoxStyle}>
        <div className='dropdown-selected-option' onClick={() => setIsOpen(!isOpen)}>
            {value}
            <span className={`dropdown-arrow ${isOpen ? 'open' : ''}`}> &#9560; </span>
        </div>
        {isOpen && (
            <div className='dropdown-options'>
                {options.map((option) => (
                    <div key={option}
                         className={`dropdown-options-option ${option === value ? 'selected' : ''}`}
                         onClick={() => handleOptionsClick(option)} >
                        {option}
                    </div>
                ))}
            </div>
        )}
    </div>
  );
};

export default DropDown;
