import React from 'react';
import './inputbox.css';

const InputBox = ({ width, height, fontSize, placeHolder, type, options, value, onChange }) => {

    const inputBoxStyle = {
        width: width,
        height: height,
        fontSize: fontSize,
    };

    const textAreaBoxStyle = {
      ...inputBoxStyle,
      padding: "0%",
      paddingTop: ".5rem",
      paddingLeft: ".5rem",
    };

    const dropDownBoxStyle = {
      width: width,
      height: height,
    };

    const handleChange = (e) => {
      const newValue = e.target.value;

      if (onChange) {
        onChange(newValue)
      }
    };


    const inputElement = type === 'textarea' ? (
      <textarea className='input-box' placeholder={placeHolder} style={textAreaBoxStyle} value={value} onChange={handleChange} />
    ) : type === 'dropdown' ? (
      <select className='input-box' value={value} onChange={handleChange}>
        {options.map((option) => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
    ) : (
      <input className='input-box' placeholder={placeHolder} style={inputBoxStyle} value={value} onChange={handleChange} /> 
    );

    return inputElement;
};

export default InputBox;
