import React from 'react';
import './inputbox.css';

const InputBox = ({ width, height, fontSize, placeHolder, type }) => {

    const inputBoxStyle = {
        width: width,
        height: height,
        fontSize: fontSize,
    };

    const textAreaBoxStyle = {
      ...inputBoxStyle,
      paddingTop: "2px",
    }


    const inputElement = type === 'textarea' ? (
      <textarea className='input-box' placeholder={placeHolder} style={inputBoxStyle} />
    ) : (
      <input className='input-box' placeholder={placeHolder} style={inputBoxStyle} />
     
    );

    return inputElement;
};

export default InputBox;
