import logo from './logo.svg';
import './App.css';
import CreateModpack from './components/CreateModpack/CreateModpack';
import { useEffect, useState } from 'react';
import ToolBarButton from './components/ToolBarButton/ToolBarButton';
import add from './assets/add.png';
import Modpack from './components/Modpack/Modpack';
import { getAllModpacks } from './util/api';

// fix button highlights

function App() {

  const [showCreateModpack, setShowCreateModpack] = useState(false);
  const [allModpacks, setAllModpacks] = useState([null]);

  const toggleShowCreateModpack = () => {
    setShowCreateModpack(!showCreateModpack);
  };

  const fetchData = async () => {
    const modpacks = await getAllModpacks();

    console.log(modpacks);
    setAllModpacks(modpacks);
  }


  const deletePack = async (modpack) => {
    console.log("Deleted: ", modpack.id);
  };

  const testModpack = allModpacks[0];

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="App">
      <ToolBarButton text={"Create New Modpack"} icon={add} onClick={toggleShowCreateModpack} />

    {showCreateModpack && (
       <div className='app-create-modpack-container'>
        <CreateModpack close={toggleShowCreateModpack} />
       </div>     
    )}

      <div className='app-modpacks'>
      {testModpack && (
        <div>
          {allModpacks.map((modpack, index) => (
            index % 4 === 0 && (
              <div className="app-modpacks-row" key={`row-${index}`}>
                {allModpacks.slice(index, index + 4).map((modpack, subIndex) => (
                  <div className="modpack" key={`modpack-${index}-${subIndex}`}>
                    <Modpack modpack={modpack} deletePack={deletePack} />
                  </div>
                ))}
              </div>
            )
          ))}
        </div>
      )}
    </div>
    </div>
  );
}

export default App;
